import {
  LOGIN,
  LOGIN_LOADER,
  LOGOUT,
  REGISTER_LOADER,
  RESET_PASSWORD,
} from "../types";

const initialState = {
  loading: false,
  loginLoading: false,
  resetPasswordLoading: false,
  user: null,
  uid: null,
};

export default function authReducer(state = initialState, { type, payload }) {
  switch (type) {
    case LOGIN:
      return {
        ...state,
        user: payload,
        uid: payload.uid,
        loginLoading: false,
      };
    case LOGIN_LOADER:
      return {
        ...state,
        loginLoading: payload,
      };
    case REGISTER_LOADER:
      return {
        ...state,
        loading: payload,
      };
    case RESET_PASSWORD:
      return {
        ...state,
        resetPasswordLoading: payload,
      };
    case LOGOUT:
      return {
        ...state,
        user: null,
        uid: null,
      };
    default:
      return state;
  }
}
